const fetch = require('node-fetch')

module.exports = {
    async Trophy1(auth) {
        return new Promise(async (resolve, reject) => {
            fetch('http://kitkabackend.eastus.cloudapp.azure.com:5010/round/finishv2/1', {
                method: 'GET',
                headers: {
                    'authorization': auth
                }
            })
                .then(res => res.text())
                .then(data => {
                    resolve(data);
                })
                .catch(err => {
                    reject(err);
                });
        })
    },
};

